package com.iteye.wwwcomy.proxy.compiler;import java.util.Random;public class TimeProxy implements Movable {	Movable m;	public Movable getM() {		return m;	}	public void setM(Movable m) {		this.m = m;	}	@Override	public void move() {		long start = System.currentTimeMillis();		m.move();		try {			Thread.sleep(new Random().nextInt(2000));		} catch (InterruptedException e) {			e.printStackTrace();		}		long end = System.currentTimeMillis();		System.out.println("Spend Time: " + (end - start) + "ms");	}}